mapper.select:
select
  content, an_id, qs_id, an_name, createtime, agreecount, disagreecount
from answer


        and content = #{content}


        and an_id = #{anId}


        and qs_id = #{qsId}


        and an_name = #{anName}


        and createtime = #{createtime}


        and agreecount = #{agreecount}


        and disagreecount = #{disagreecount}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from answer


        and content = #{content}


        and an_id = #{anId}


        and qs_id = #{qsId}


        and an_name = #{anName}


        and createtime = #{createtime}


        and agreecount = #{agreecount}


        and disagreecount = #{disagreecount}
